import { useContext } from 'react';
import ThemeContext from '@theme/ThemeContext';

// TODO: Un-stub the theme context (#3730)
function useThemeContext() {
  const context = useContext(ThemeContext);
  return context == null ? {
    isDarkTheme: false
  } : context;
}

export default useThemeContext;